mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, v.req_id, v.req_date, v.start_date, v.end_date, v.req_days, v.comments, v.status, v.aprv_date,v.file_path, e.emp_id, e.name, e.position, e.p_email, e.c_email, e.tel, e.mgr_id, e.dept_id, vt.* FROM vc_req v JOIN emp e ON v.emp_id = e.emp_id JOIN vc_type vt ON v.type_id = vt.type_id WHERE ROWNUM <= #{pageNum} * #{amount}
AND e.user_id = #{userId}
) WHERE RN > (#{pageNum}-1) * #{amount}

SELECT
*
FROM (
SELECT
ROWNUM RN, v.*, e.name,e.p_email,e.c_email, e.position,e.hire_date
FROM
vc_req v JOIN emp e ON v.emp_id = e.emp_id
WHERE
 ROWNUM <= #{pageNum} * #{amount} 
AND e.leave_date IS NULL

    AND e.dept_id = #{deptId}


    AND v.STATUS = #{keyword}


)
 WHERE RN > (#{pageNum}-1) * #{amount}

mapper.update:
UPDATE vc_req SET status = #{status}

    , APRV_DATE = #{aprvDate}


    , DENIED_COMMENTS = #{deniedComments}

WHERE req_id = #{reqId}

mapper.select:
SELECT COUNT(*) FROM VC_REQ v,EMP e
WHERE v.emp_id = e.emp_id

    AND e.dept_id = #{deptId}


    AND v.STATUS = #{keyword}
